import os
import sys

def StringComposition(k, dna):
    kmers = []
    for i in range (len(dna) - k + 1):
        substring = dna[i : i + k]
        kmers.append(substring)
    return kmers

k = 100
dna = ""
res = StringComposition(k, dna)
with open('output.txt', 'w') as file:
    for kmer in res:
        file.write(kmer + " ")
    